<?php namespace Doula_Course\App\Tmpl;if ( ! defined( 'ABSPATH' ) ) { exit; }// 	This is not a standard Wordpress template, but called by the material_loop() in the /func/post_types.php file in the doula_course plugin. 		global $current_user;	$user_id = $current_user->ID;	// Determin Course Access 	$umeta_key = 'course_access';	$usingle = true;	$ca_num = intval( get_user_meta( $user_id, $umeta_key, $usingle ) );	$acc_arr = array(		1 => "main course only",		2 => "main and childbirth courses",		3 => "all course materials"	);	if( ( $ca_num >= 1 ) && ( $ca_num <= 3 ) ){		$status_message .= "You currently have access to <strong>".$acc_arr[$ca_num]."</strong>.";	}	$manuals_arr = array(		'main' => 'Main Course',		'maternal-postpartum' => 'Maternal Postpartum Physiology',		'newborn-physiology' => 'Newborn Physiology',		'newborn-care' => 'Newborn Care',		'household-care' => 'Household Care'	);		$stud_mat_arr = array();	switch( $ca_num ){		case 3:			$stud_mat_arr[] = 'doula-actions';					case 2:			array_unshift( $stud_mat_arr, 'childbirth' );					case 1:			array_push( $stud_mat_arr, 'case-studies', 'birth-packet');			array_unshift( $stud_mat_arr,  'main' );			break;					case 0:		default:			$status_message .= "You do not have access to these materials.";			break;	}						// Determine Account Active Status	$s_active = current_user_can('student_current');	$site_url = home_url();		$code = '';	if( !$s_active ){ 		$code .= "<p>Hmm, Looks like your account is marked as <em>inactive</em>. <a href='$site_url/inactive-student-notice'>Get details.</a></p>";	} else {		$code .=  "<p>Current course manuals are now available online in web format. First-time students, START with <a href='$site_url/manuals/postpartum-doula/main/' >the MAIN COURSE manual</a>. </p>				<ul id='section-list'>			<li class='ct_cert page_item'>						<a href='/manuals/postpartum-doula/'>				<span class='ct_name'>CERTIFICATION</span>				<span class='course_title'>Postpartum Doula Training</span>			</a>			</li>		</ul>  		";	}		?>			<div <?php post_class(); ?> > 			<div class="course-nav top">				<a href="/">Home</a>			</div><!-- end .course-nav btm -->						<div class="post-entry">								<?php 				echo $code;				?>								</div><!-- end of .post-entry -->						<div class="course-nav btm">				<a href="/">Home</a>			</div><!-- end .course-nav btm -->		</div><!-- end  -->   			